<?php
namespace App\Http\Requests;use Illuminate\Foundation\Http\FormRequest;class LotRequest extends FormRequest{public function authorize():bool{return true;}public function rules():array{$mode=$this->input('mode','single');$rules=['project_id'=>['required','exists:projects,id'],'area'=>['required','integer','min:0'],'base_price'=>['required','integer','min:0'],'status'=>['required','in:available,sold,reserved,active'],];if($mode==='bulk'){$rules['bulk_prefix']=['required','string','max:20'];$rules['bulk_start']=['required','integer','min:1'];$rules['bulk_end']=['required','integer','gte:bulk_start'];$rules['bulk_suffix']=['nullable','string','max:10'];}else{$rules['block_number']=['required','string','max:50'];}return $rules;}}